          <i class="bi bi-reply me-1"></i>Отговори
        </a>
      </div>
      <p class="mb-1 mt-1">${escapeHtml(comment.body)}</p>
      ${comment.replies.map((r) => renderComment(r, depth + 1)).join('')}
    </div>
  `;
  return html;
}
